/**
 * Combines the specified deterministic generators to return a new
 * deterministic generator that generates all of the values generated by the
 * specified generators.
 *
 * @tsplus static effect/core/testing/Gen.Ops concatAll
 * @category constructors
 * @since 1.0.0
 */
export function concatAll<R, A>(gens: Iterable<Gen<R, A>>): Gen<R, A> {
  return Gen.suspend(
    Array.from(gens).reduce(
      (acc, curr) => acc.concat(curr),
      Gen.empty as Gen<R, A>
    )
  )
}
